                          public function DisplayDetails(WebPage $oPage, $bEditMode = false)
                          {
                            if (empty($this->Get('monitoring_url')) && !empty($this->Get('monitoring_id')))
                            {
                               $sMonitoringURL = MetaModel::GetModuleSetting ( 'Monitoring', 'monitoring_url', '' );
							   $sHostURL = $sMonitoringURL . 'main.php?p=10299&idcompany=2#hostId=' . $this->Get ( 'monitoring_id' );
                               $this->Set('monitoring_url', $sHostURL);
                            }
                            parent::DisplayDetails($oPage, $bEditMode);
                          }

protected function OnUpdate()
				{
			        //On reset l'url qui est calcule uniquement a l'affichage
			        $this->Set('monitoring_url', '');
			        parent::OnUpdate();
				}
