method.code:
        protected function MakeTicketRef($iNextId)
        {
                switch(get_class($this))
                {
                        case 'UserRequest':
                        $sFormat = 'R-MCTO-%06d';
                        break;

                        case 'Incident':
                        $sFormat = 'I-MCTO-%06d';
                        break;

                        case 'Change':
                        case 'RoutineChange':
                        case 'EmergencyChange':
                        case 'NormalChange':
                        $sFormat = 'C-MCTO-%06d';
                        break;

                        case 'Problem':
                        $sFormat = 'P-MCTO-%06d';
                        break;

                        case 'Project':
                        $sFormat = 'J-MCTO-%06d';
                        break;

						case 'ProjectChange':
                        $sFormat = 'JC-MCTO-%06d';
                        break;

                        default:
                        $sFormat = 'T-MCTO-%06d';
                }
                return sprintf($sFormat, $iNextId);
        }
